INSERT INTO `sys_log` VALUES ('309', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"技术难题\",\"cooperationType\":\"技术许可\",\"cooperationIntention\":\"技术先进型服务企业\",\"demandIndustry\":\"制造业\",\"demandIndustryDetail\":\"电器机械和器材制造业\",\"demandOutline\":\"电磁继电器单绕组骨架式线圈组自动生产线\",\"demandContent\":\"研制、建设一条可覆盖电磁继电器单绕组骨架式线圈组的自动化生产线，生产线应具备绝缘薄膜自动分切、线圈自动绕制、线圈始末端漆包线无损式自动去漆等功能。\",\"expectedResult\":\"详细的技术需求：\\n\\n1、薄膜自动分切：可将厚度为0.01mm、0.0125mm、0.02mm的绝缘薄膜分切为宽（1.2～20）mm，长（50～1300）mm，宽度方向精度±0.1mm，长度方向±1mm。\\n\\n2、线圈自动绕制：可覆盖线径（0.016～0.51）mm漆包线的绕制，匝数（1～19999）任意可设，绕线转速（20～9000）rpm任意可设。\\n\\n3、漆包线无损去漆：采用无损（非机械式）去漆方式，可去除线径（0.016～0.51）mm高耐温等级漆包线（牌号QY-1/220）的漆皮。去漆长度（10～50）mm可设，去漆长度精度±0.5mm。\",\"contact\":\"李总\",\"phoneNum\":\"18726376565\",\"preInvestmentAmount\":300,\"endDate\":\"2020-11-13\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '12', '192.168.30.29', '2019-11-08 10:07:02');
INSERT INTO `sys_log` VALUES ('310', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"其他\",\"cooperationType\":\"其他\",\"cooperationIntention\":\"其他\",\"demandIndustry\":\"制造业\",\"demandIndustryDetail\":\"专用设备制造业\",\"demandOutline\":\"大截面试样在人工海水加电位条件下的环境助长裂纹试验\",\"demandContent\":\"为评价高强度系泊链在实际使用条件下的抗环境脆化性能，需要使用25mm厚紧凑拉伸试样测定K1esc值。\",\"expectedResult\":\"试验按照ASTM EI820或者等同标准进行，实验分别在人工海水（按照ASTM D1141 )中按照无腐蚀电位、-950mv（sce）电位和-1050mv（sce）电位下进行，试验前式样要预充氢48小时，试验速率10²m/s，速率更低也可以。试验结果以形式报告说明环境的敏感性。\",\"contact\":\"李总\",\"phoneNum\":\"15674637262\",\"preInvestmentAmount\":5,\"endDate\":\"2019-12-05\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '12', '192.168.30.29', '2019-11-08 10:08:08');
