INSERT INTO `sys_log` VALUES ('312', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"合作开发\",\"cooperationType\":\"技术许可\",\"cooperationIntention\":\"高新技术企业\",\"demandIndustry\":\"水利环境公共设施管理\",\"demandIndustryDetail\":\"生态保护和环境治理业\",\"demandOutline\":\"寻求一种双曝气好氧双向回流污泥沉淀反应装置的研究\",\"demandContent\":\"因氮过量排放所引起的水体富营养化是目前最为关注的环境问题之一，氨氮(总氮)在国家确定的水污染防治重点流域、海域专项规划中成为总量控制指标\",\"expectedResult\":\"我国最新颁布的污水排放标准(GB18917-2002)中的一级标准要求所有排污单位出水氨氮小于 5mg/L，总氮(TN)小于 15mg/L。而本研究项目提供一种双曝气好氧双向回流污泥双污泥过滤区的高效反应器，反应器具有污泥产量少、有机物容积负荷高、有机物去除率高 、脱氮能力强、结构紧凑、制造简单、运行控制容易、占地面积小等优点。4401HAC030\",\"contact\":\"李总\",\"phoneNum\":\"19827362837\",\"preInvestmentAmount\":300,\"endDate\":\"2019-11-30\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '7', '192.168.30.29', '2019-11-08 10:10:53');
