INSERT INTO `sys_log` VALUES ('585', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '{\"id\":99,\"userName\":\"ceshi01\",\"resultTechnolo\":\"一种利用水锤效应的能量产生动能的装置\",\"stage\":\"其他\",\"advantages\":\"提高效率\",\"industry\":\"先进制造与自动化\",\"industryDetail\":\"工业生产过程控制系统\",\"attribute\":\"企业合作\",\"patenNname\":\"一种利用水锤效应的能量产生动能的装置\",\"patenNumber\":\"2015100043995\",\"price\":700,\"registerNumber\":\"无\",\"describe\":\"本发明涉及一种利用水锤效应的能量产生动能的装置，包括壳体上壳体、中间壳体、凹槽飞轮、转轮、摆轮、阀门、主轴、回压室和下壳体，中间壳体处于下壳体的上部，上壳体处于中间壳体的上部，中间壳体的底面将下壳体的腔体密封起来，下壳体的密封腔体形成压力室，转轮处于压力室内，水压通过叶片带动转轮持续转动，转轮的转动通过主轴带动凹槽飞轮转动，还通过主轴输出动能\",\"appliedRange\":\"本发明提供的一种利用水锤效应的能量产生动能的装置，将水锤效应带来巨大的能量转化成持续的动能输出，用于发电或者其他领域，节约了能源，同时避免其他能量消耗带来的环境污染问题。\",\"transferType\":\"技术入股\",\"linkMan\":\"李总\",\"phoneNumber\":\"18625362762\"}', '12', '192.168.30.25', '2019-11-12 11:19:17');
INSERT INTO `sys_log` VALUES ('586', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"技术难题\",\"cooperationType\":\"技术许可\",\"cooperationIntention\":\"不限\",\"demandIndustry\":\"新材料\",\"demandIndustryDetail\":\"金属材料\",\"demandOutline\":\"研究开发一种可在较高温度下具备一定的抗强碱腐蚀能力的合金材料\",\"demandContent\":\"。目前厂房基础建设基本完成，配套的配电房已经竣工供电，主设备和辅助设备已经安装调试，已经开展联机运行和试制，预计2019年第一季度开展核级中小型锻件模拟件试制及检验试验。现公司需求合金材料的相关专业技术人才，能够研究开发一种可在较高温度下具备一定的抗强碱腐蚀能力的合金材料。\",\"expectedResult\":\"公司在建重点项目：核级中小型锻件快径锻造生产，已投入资金约1.6亿元。该项目主要产品是核级耐高温耐腐蚀不锈钢、高温合金、结构钢等中小型锻件。公司结合国内核电工程、航空和军工等领域对高质量高安全性能的核级中小型锻件国产化的需求，配备国外先进的快径锻造技术和快径锻造生产设备，研制生产直径￠250～￠800之间高品质特殊性能中小型锻件产品\",\"contact\":\"李总\",\"phoneNum\":\"19827382734\",\"preInvestmentAmount\":\"面议\",\"endDate\":\"2019-11-30\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '11', '192.168.30.25', '2019-11-12 11:23:27');
INSERT INTO `sys_log` VALUES ('587', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"技术难题\",\"cooperationType\":\"技术许可\",\"cooperationIntention\":\"高新技术企业\",\"demandIndustry\":\"新材料\",\"demandIndustryDetail\":\"金属材料\",\"demandOutline\":\"热浸镀铝锌产品研发\",\"demandContent\":\"目前热浸镀锌技术已被广泛应用，成为最主要的钢材防腐技术之一，而热浸镀铝由于工艺复杂，产品使用具有一定局限性，为改善提高镀锌技术，克服镀铝技术不足，研发出热浸镀铝锌产品，铝锌融合，会使钢板表面产生锌花。钢板行业低迷，原材料价格波动大，20%镀铝锌产品应运而生，由于工艺简单，原料差，炉火热力不足等原因导致市场钢板锌花大小不一，正反不一。\",\"expectedResult\":\"通过添加一定配比的锶钛两元素，达到细化均匀晶核、增加粘附性的作用，使钢板表面锌花均匀。公司望通过产学研合作方式或引进科技人才来对本项目进行计划和设计，并有效实施。\",\"contact\":\"李总\",\"phoneNum\":\"15873647381\",\"preInvestmentAmount\":\"面议\",\"endDate\":\"2019-11-29\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '15', '192.168.30.25', '2019-11-12 11:25:58');
INSERT INTO `sys_log` VALUES ('588', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"合办企业\",\"cooperationType\":\"技术入股\",\"cooperationIntention\":\"技术先进型服务企业\",\"demandIndustry\":\"新材料\",\"demandIndustryDetail\":\"无机非金属材料\",\"demandOutline\":\"多孔介质同时负载膨胀阻燃剂和纳火金属氧化物阻燃材料的研发\",\"demandContent\":\"目前存在强断裂伸长率小、氧指数低、阻燃效果一般的现象。现寻求能够研发多孔介质同时负载膨胀阻燃剂和纳火金属氧化物阻燃材料，从而提高高阻燃效率、耐热老化、防水、耐酸碱、抗弯曲、抗拉、柔韧、抗撕裂及耐磨等高性能的充电桩用电缆护套料\",\"expectedResult\":\"公司正在研发的“新能源汽车充电桩用低烟无卤高阻燃聚氨酯护套料”\",\"contact\":\"李总\",\"phoneNum\":\"13874657363\",\"preInvestmentAmount\":\"90\",\"endDate\":\"2019-11-29\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '7', '192.168.30.25', '2019-11-12 11:26:53');
INSERT INTO `sys_log` VALUES ('589', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"人才需求\",\"cooperationType\":\"技术入股\",\"cooperationIntention\":\"技术先进型服务企业\",\"demandIndustry\":\"新材料\",\"demandIndustryDetail\":\"高分子材料\",\"demandOutline\":\"静电膜层数升级的技术\",\"demandContent\":\"静电膜主要是PE静电膜为主，静电膜一般采用多种材料三层共挤或者多层共挤同步而制成的表面保护膜，以PE料为基材，其中一层是EVA自粘性材料，这种材质属于低分子高聚物，当保护膜的表面聚集大量的低分子高聚物的时候，这些低分子高聚物会与被贴产品表面的细小凹坑互相镶嵌在一块，从而导致可以不涂布胶水也可以与产品粘贴一起，这就是所谓的静电吸附粘贴。\",\"expectedResult\":\"在市面上静电膜也可称之为自粘膜，这种静电保护膜无需涂布胶水，安全环保，易粘易贴，静电吸附，以静电吸附粘贴的保护膜是以后保护膜发展的方向。此项目目前处于研发阶段，希望有相关专家进行静电膜层数升级的技术合作。\",\"contact\":\"王总\",\"phoneNum\":\"19827382738\",\"preInvestmentAmount\":\"面议\",\"endDate\":\"2019-11-30\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '17', '192.168.30.25', '2019-11-12 11:29:45');
INSERT INTO `sys_log` VALUES ('590', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"科技服务\",\"cooperationType\":\"技术入股\",\"cooperationIntention\":\"技术先进型服务企业\",\"demandIndustry\":\"电子信息\",\"demandIndustryDetail\":\"软件\",\"demandOutline\":\"安全运检管理软件系统开发\",\"demandContent\":\"需求现状：安全运检管理软件系统主要用于工业设备运行风险管理领域，目前工业设备运行风险识别面临以下问题：工作量大、智能化水平低、准确率和效率低等。本系统开发的目的是为了在互联网+基础上，通过运用移动互联网、物联网、云计算等技术，利用物联网监控设备采集的视频图像数据和传感数据对设备运行状态进行实时性或周期性分析，实现对设备运行风险的动态识别，为设备运行提供安全保障。\",\"expectedResult\":\"设备安全系统进行管理，结合传感、识别、网络技术的创新，实现高性能安全运检管理软件开发\",\"contact\":\"李总\",\"phoneNum\":\"19827387283\",\"preInvestmentAmount\":\"30\",\"endDate\":\"2019-11-29\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '18', '192.168.30.25', '2019-11-12 11:33:35');
INSERT INTO `sys_log` VALUES ('591', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '{\"id\":100,\"userName\":\"ceshi01\",\"resultTechnolo\":\"平流层飞艇并联式温度控制系统及控制方法\",\"stage\":\"试验阶段\",\"advantages\":\"其他\",\"industry\":\"航空航天\",\"industryDetail\":\"航天\",\"attribute\":\"院企合作\",\"patenNname\":\"平流层飞艇并联式温度控制系统及控制方法\",\"patenNumber\":\"CN201410247741.X\",\"price\":90,\"registerNumber\":\"无\",\"describe\":\"本发明公开了一种平流层飞艇并联式温度控制系统及控制方法，所述控制系统包括并联的第一温度控制子系统和第二温度控制子系统\",\"appliedRange\":\"第一温度控制子系统包括高压气罐，安装于飞艇机舱内并与相关设备联接或接近的冷板，安装于飞艇机舱内的换热器，靠近换热器的第一风扇，连通高压气罐与冷板并将气体引出机舱的第一管路，以及连通高压气罐与换热器并将气体引出机舱的第二管路；第二温度控制子系统包括风机，连通风机与冷板并将气体引出机舱的第三管路，设置于机舱内相关设备上方的出风装置，以及连通出风装置与风机的第四管路。本发明耗电量较少、质量轻、对管路的密封性要求相对较低；能防止飞艇在极热天气低空时单一冷源不能满足制冷需求的不利影响。\",\"transferType\":\"技术许可\",\"linkMan\":\"李总\",\"phoneNumber\":\"19827382736\"}', '16', '192.168.30.25', '2019-11-12 11:35:40');
