INSERT INTO `sys_log` VALUES ('594', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"技术难题\",\"cooperationType\":\"技术提成\",\"cooperationIntention\":\"高新技术企业\",\"demandIndustry\":\"新能源与节能\",\"demandIndustryDetail\":\"高效节能技术\",\"demandOutline\":\"质子交换膜与薄膜太阳能电池材料\",\"demandContent\":\"当前技术概况：开发高性能燃料电池膜电极与薄膜太阳能电池的制备工艺\\n拟解决的问题： 1、开发高性能燃料电池膜电极与薄膜太阳能电池的制备工艺\\n 2、开发铂碳催化剂与薄膜太阳能电池墨水\",\"expectedResult\":\"提高催化剂中Pt的活性和薄膜太阳能电池效率并制备出性能达到商业化水平墨水\\n开发超强酸质子交换膜材料并用于 提升现有膜电极产品的性能（现有产品在常温常压冷气条件下功率密度为0.4w,在加温加压水冷条件下功率密度为0.8w，薄膜太阳能电池1.2m*0.6m面积电池效率达11%以上。\",\"contact\":\"李总\",\"phoneNum\":\"18726372837\",\"preInvestmentAmount\":\"40\",\"endDate\":\"2019-11-28\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '8', '192.168.30.25', '2019-11-12 11:38:48');
INSERT INTO `sys_log` VALUES ('595', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"其他\",\"cooperationType\":\"其他\",\"cooperationIntention\":\"其他\",\"demandIndustry\":\"资源与环境\",\"demandIndustryDetail\":\"清洁生产技术\",\"demandOutline\":\"关于生态鱼缸中水质酸化后的处理\",\"demandContent\":\"生态鱼缸中，鱼缸的水质会随着时间不断变酸（鱼的饲料、粪便等会产生亚硝酸盐类，经过过滤系统中的消化细菌处理后，转化成硝酸盐类，从而水质变酸），需要大约一个星期左右换一次水进行稀释\\n拟解决的问题：针对这个问题的主要需求为：针对水质变酸，用物理或者化学的方式进行处理，减缓水质酸化的速度，从而减少换水的频率\",\"expectedResult\":\"预计解决方式：用物理或者化学的方式进行处理，减缓水质酸化的速度\\n具体指标要求：减少换水的频率，保持鱼缸水质长期稳定\",\"contact\":\"李总\",\"phoneNum\":\"19828372734\",\"preInvestmentAmount\":\"30\",\"endDate\":\"2019-11-30\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '14', '192.168.30.25', '2019-11-12 11:39:54');
