INSERT INTO `sys_log` VALUES ('598', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '{\"id\":104,\"userName\":\"ceshi01\",\"resultTechnolo\":\"一种平流层飞艇副气囊充放气系统及方法\",\"stage\":\"可以量产\",\"advantages\":\"降低成本\",\"industry\":\"航空航天\",\"industryDetail\":\"航空\",\"attribute\":\"企业合作\",\"patenNname\":\"一种平流层飞艇副气囊充放气系统及方法\",\"patenNumber\":\"CN201610569034.1\",\"price\":70,\"registerNumber\":\"无\",\"describe\":\"本发明公开了一种平流层飞艇副气囊充放气系统，所述副气囊的进出口连接有换热器、第六阀门和第一风机，其中第一风机的进口设置有第二电加热器，第一风机的出口通过第五阀门连接笛形管，第一风机的出口还通过第四阀门与副气囊的进出口相连通，换热器的出口B还通过管路依次连接有第二阀门、第一电加热器和第一阀门，第一电加热器的进口C还通过第三阀门与副气囊的进出口相连通\",\"appliedRange\":\"本发明在保证飞艇副气囊正常工作的前提下为飞艇舱内的电子设备提供了一定的制冷量，同时利用舱内电子设备的发热以及在管路上增加电加热器为气体加热，避免了系统中阀门、风机等设备结冰故障的发生。该系统结构简单、冗余度高。本发明还公开了系统的充放气方法。\",\"transferType\":\"技术许可\",\"linkMan\":\"李总\",\"phoneNumber\":\"19872837267\"}', '9', '192.168.30.25', '2019-11-12 11:55:35');
