INSERT INTO `sys_log` VALUES ('712', 'ceshi01', '企业需求', '新增企业需求', 'com.example.busniess.controller.DemandsController.addDemands()', '{\"userName\":\"ceshi01\",\"demandType\":\"科技服务\",\"cooperationType\":\"技术许可\",\"cooperationIntention\":\"高新技术企业\",\"demandIndustry\":\"航空航天\",\"demandIndustryDetail\":\"航天\",\"demandOutline\":\"C919飞机后机身前段下壁板高温固化后变形超差的仿真预测分析\",\"demandContent\":\"C919后机身壁板为帽型加筋壁板结构，帽型长桁随着机身壁板的外形呈双曲形式，零件采用共固化的工艺方式制造。因零件结构特性及本身的回弹，零件固化后会出现轮廓度及帽型长桁轴线度超差的问题。如，C919后机身前段下壁板为双曲面结构帽型加筋壁板，长度3047mm，大端宽2401mm，小端宽1673mm，帽型长桁数量14根，帽型筋长度2898mm。下壁板采用层间增韧环氧树脂碳纤维单向带和织物预浸料铺贴，固化温度180±6℃，固化压力0.6MPa。固化后由于零件的结构特性以及本身的回弹，导致其轴线度和轮廓度出现超差问题，要求零件轴线度±1mm，轮廓度0.75mm，实际检测结果零件轴线度最大-3.786mm，超差-2.786mm，轮廓度1.314mm，超差0.564。通过固化变形仿真技术研究，能预测下壁板零件固化变形趋势，对工装型面设计及工装型面返修做出指导，从而生产出轮廓度及帽型长桁轴线合格的零件。\",\"expectedResult\":\"希望通过此项技术的研究，与后期零件的制造相结合，积累数据，使壁板零件达到帽型筋轴线不超过±1mm，轮廓度不超过0.75mm的技术指标。\",\"contact\":\"王总\",\"phoneNum\":\"15684738276\",\"preInvestmentAmount\":\"60\",\"endDate\":\"2019-11-30\",\"status\":0,\"approvalStatus\":0,\"counts\":0}', '9', '192.168.30.25', '2019-11-14 08:56:33');
