INSERT INTO `sys_log` VALUES ('1325', 'ceshi01', '专家入驻', '新增专家入驻', 'com.example.busniess.controller.ProfessionalsController.addProfessionals()', '[{\"userName\":\"ceshi01\",\"realName\":\"1\",\"institutions\":\"1\",\"positions\":\"1\",\"engagedIndustryDetail\":\"\",\"industryExperience\":\"1\",\"technologyScope\":\"生物与新医药\",\"technologyScopeDetail\":\"中药、天然药物\",\"outstandingContribution\":\"\",\"researchDirection\":\"1\",\"introduced\":\"\\u003cp\\u003e余志晟，教授，博士生导师，现为中国科学院大学\\u0026nbsp;资源与环境学院教师。\\u003c/p\\u003e\\u003cp\\u003e2003年6月毕业于中国科学院生态环境研究中心，获理学博士学位。\\u003c/p\\u003e\\u003cp\\u003e2012年8月至2013年8月英国牛津大学（University\\u0026nbsp;of\\u0026nbsp;Oxford）工程科学系,访问研究。\\u003c/p\\u003e\\u003cp\\u003e2009年3月至2010年3月以色列希伯来大学（Hebrew\\u0026nbsp;University\\u0026nbsp;of\\u0026nbsp;Jerusalem）生命科学院，博士后。\\u003c/p\\u003e\\u003cp\\u003e2003年8月至2005年8月清华大学环境学院,\\u0026nbsp;博士后。\\u003c/p\\u003e\\u003cp\\u003e\\u003cbr\\u003e\\u003c/p\\u003e\\u003cp\\u003e目前围绕“现代环境微生物学在环保工程和生物技术的应用”开展：\\u003c/p\\u003e\\u003cp\\u003e（1）\\u0026nbsp;生物质与生物能，包括酒发酵生产的微生物学机理、纤维素生物乙醇转化，煤层气和页岩气生物成因机制等；\\u003c/p\\u003e\\u003cp\\u003e（2）生物降解与生物修复，包括特定工业污染物如染料、农药等的生物降解，油田和农田污染土壤的生物修复等；\\u003c/p\\u003e\\u003cp\\u003e（3）水处理与水供给，承担污水处理、饮用水工程项目建设，并解决工程运行过程中的微生物技术问题；\\u003c/p\\u003e\\u003cp\\u003e（4）微生物和工业酶制剂的研究与开发，如食品保健品益生菌，环保微生物制剂等的研究工作。\\u003c/p\\u003e\",\"phoneNum\":\"15073362141\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"西城区\",\"iconAddress\":\"/img/2019-12-20/d42072eea584417181fc3a4a6ba097cd.jpg\",\"certificateAddress\":\"/img/2019-12-20/e384c2231d56488785974b4b92e8015e.jpg\",\"status\":0,\"approvalStatus\":0,\"createTime\":\"Dec 20, 2019 10:05:10 AM\",\"pinYin\":\"1 \"}]', '58', '192.168.30.38', '2019-12-20 10:05:11');
