INSERT INTO `sys_log` VALUES ('1394', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":311,\"userName\":\"ceshi01\",\"resultTechnolo\":\"智能控释的纳米农药缓释剂型的研发\",\"stage\":\"可以量产\",\"advantages\":\"实用新型专利\",\"industry\":\"新材料\",\"industryDetail\":\"生物医用材料\",\"attribute\":\"企业合作\",\"patenNname\":\"智能控释的纳米农药缓释剂型\",\"patenNumber\":\"1231627356.12\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\t水稻纹枯病是遍布全球的水稻重要病害之一，巴斯夫针对第一大杀菌剂吡唑醚菌酯开发了对水生生物低毒且防效优良的新剂型——稻清®。然而，稻清®无法实现药物在靶标生物体内的智能控释，仍存在一定的安全隐患。因此，发展吡唑醚菌酯的智能控释技术是一个非常有意义的研究课题。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t本项目基于靶标生物和非靶标生物体内微环境的差异性来构建纳米载药系统，从而达到既降低吡唑醚菌酯对水生生物毒性，又提高其对靶的精准释放效率，进而实现减少用药量、提高防治效果等目的。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"李\",\"phoneNumber\":\"19827334726\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":311,\"img\":\"/img/2019-12-27/7e1636dfc5c847e1811e015f92e5bcbc.png\"},{\"oId\":311,\"img\":\"\"},{\"oId\":311,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '23', '192.168.30.29', '2019-12-27 12:15:51');
INSERT INTO `sys_log` VALUES ('1395', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":312,\"userName\":\"ceshi01\",\"resultTechnolo\":\"高温加载下测量薄膜光学常数及形貌参数的装置及技术\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"航空航天\",\"industryDetail\":\"航空\",\"attribute\":\"企业合作\",\"patenNname\":\"高温加载下测量薄膜光学常数及形貌参数的装置及技术\",\"patenNumber\":\"127346751254.09\",\"price\":40,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t一种高温加载下测量薄膜光学常数及形貌参数的装置及方法。所述装置包括光学常数测量模块和高温加热模块，高温加热模块包括高温加热腔、石英玻璃窗、高温加热台、高温台控制器以及气体流道单元，光学常数测量模块包括入射光路单元和反射光路单元。所述方法包括根据校准的椭偏参数获取样品的真实椭偏参数，并根据样品的光学常数参数化模型和正向光学模型获取样品光学常数以及形貌参数\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t所述方法包括根据校准的椭偏参数获取样品的真实椭偏参数，并根据样品的光学常数参数化模型和正向光学模型获取样品光学常数以及形貌参数。\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"李\",\"phoneNumber\":\"18726372637\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":312,\"img\":\"/img/2019-12-27/e9de6dd7678b47bfae27635e1cd2eddb.png\"},{\"oId\":312,\"img\":\"\"},{\"oId\":312,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '10', '192.168.30.29', '2019-12-27 12:17:44');
INSERT INTO `sys_log` VALUES ('1396', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":313,\"userName\":\"ceshi01\",\"resultTechnolo\":\"紫外光电探测器及其制备技术\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"新材料\",\"industryDetail\":\"无机非金属材料\",\"attribute\":\"院企合作\",\"patenNname\":\"紫外光电探测器及其制备技术\",\"patenNumber\":\"123767858324.09\",\"price\":300,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t本发明属于微纳制造相关技术领域，其公开了一种紫外光电探测器及其制备方法，该制备方法包括以下步骤：(1)采用光刻套刻工艺和蒸发镀膜工艺在带有氮化硅绝缘层的硅基底上制备电极层；(2)在电极层上制备紫外探测薄膜，所述紫外探测薄膜是Mn掺杂CsPbBr3xCl3(1‑x)紫外探测薄膜，其是通过在所述电极层上依次蒸镀PbCl2层、CsBr层、CsCl层和MnCl2层而形成的；其中，x为0.05～0.1；(3)采用滴涂方式在所述紫外探测薄膜上制备封装保护层，以将所述紫外探测薄膜进行封装，由此得到所述紫外光电探测器。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t采用滴涂方式在所述紫外探测薄膜上制备封装保护层，以将紫外探测薄膜进行封装，由此得到所述紫外光电探测器。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"李\",\"phoneNumber\":\"17632738273\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":313,\"img\":\"/img/2019-12-27/4c14dabad1154d26a9f8d953e108c9ba.png\"},{\"oId\":313,\"img\":\"\"},{\"oId\":313,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '9', '192.168.30.29', '2019-12-27 12:19:52');
INSERT INTO `sys_log` VALUES ('1397', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":314,\"userName\":\"ceshi01\",\"resultTechnolo\":\"基于声波探测的非侵入分布式光纤管道监测系统及技术\",\"stage\":\"可以量产\",\"advantages\":\"实用新型专利\",\"industry\":\"先进制造与自动化\",\"industryDetail\":\"先进制造工艺与装备\",\"attribute\":\"独立完成\",\"patenNname\":\"基于声波探测的非侵入分布式光纤管道监测系统及技术\",\"patenNumber\":\"127368174.02\",\"price\":40,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t基于声波探测的非侵入分布式光纤管道监测系统及方法，系统包括依次连接的光源发生模块、分布式传感光纤、数据采集模块和数据处理模块，分布式传感光纤设置于待测管道外壁并沿管道轴向分布。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t方法包括采集分布式传感光纤的由流体和管壁相互作用所得到声波时域信号，经差分处理后再利用傅里叶变换得到频域信号的第一频段和第\\t二频段及其对应的第一峰值频率和第二峰值频率、第一功率谱强度和第二功率谱强度。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"李\",\"phoneNumber\":\"18736473647\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":314,\"img\":\"/img/2019-12-27/5b5fb117b55646b28d4007c0962c88d9.png\"},{\"oId\":314,\"img\":\"\"},{\"oId\":314,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '9', '192.168.30.29', '2019-12-27 12:21:13');
INSERT INTO `sys_log` VALUES ('1398', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":315,\"userName\":\"ceshi01\",\"resultTechnolo\":\"基于镁掺杂二氧化钛的钙钛矿太阳能电池及其制备技术\",\"stage\":\"试验阶段\",\"advantages\":\"发明专利\",\"industry\":\"新能源与节能\",\"industryDetail\":\"高效节能技术\",\"attribute\":\"独立完成\",\"patenNname\":\"基于镁掺杂二氧化钛的钙钛矿太阳能电池及其制备技术\",\"patenNumber\":\"23465672347.07\",\"price\":70,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t基于镁掺杂二氧化钛的钙钛矿太阳能电池的制备方法，该方法包括以下步骤：(1)对导电基底进行清洗处理；(2)采用水浴法在所述导电基底上沉积金红石相的镁掺杂二氧化钛薄膜，并进行退火处理，以形成光阳极，退火温度为100℃～120℃；(4)在所述光阳极的表面旋涂钙钛矿前驱体溶液，以得到光吸收层；(5)在所述光吸收层的表面蒸镀一层酞菁铜，以形成空穴传输层；(6)通过丝网印刷商用碳桨成膜来在所述空穴传输层上形成碳对电极层，由此结束。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t基于镁掺杂二氧化钛的钙钛矿太阳能电池的制备方法，该方法包括以下步骤：(1)对导电基底进行清洗处理；(2)采用水浴法在所述导电基底上沉积金红石相的镁掺杂二氧化钛薄膜，并进行退火处理，以形成光阳极，退火温度为100℃～120℃；(4)在所述光阳极的表面旋涂钙钛矿前驱体溶液，以得到光吸收层；(5)在所述光吸收层的表面蒸镀一层酞菁铜，以形成空穴传输层；(6)通过丝网印刷商用碳桨成膜来在所述空穴传输层上形成碳对电极层，由此结束。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"李\",\"phoneNumber\":\"18736273736\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":315,\"img\":\"/img/2019-12-27/ee992587a4c943b89d3ecb640379a1f5.png\"},{\"oId\":315,\"img\":\"\"},{\"oId\":315,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '7', '192.168.30.29', '2019-12-27 12:26:01');
INSERT INTO `sys_log` VALUES ('1399', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":316,\"userName\":\"ceshi01\",\"resultTechnolo\":\"微波辅助合成硫醚类化合物的方法\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"新能源与节能\",\"industryDetail\":\"核能及氢能\",\"attribute\":\"企业合作\",\"patenNname\":\"微波辅助合成硫醚类化合物的方法\",\"patenNumber\":\"12387682734.3\",\"price\":40,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t碳-硫键的构建在有机合成化学中占有相当重要的地位。一些1,3-二硫化合物表现出很好的生物活性，包括抗病毒、防辐射、抗肿瘤、抗痉挛以及抗艾滋病毒等活性，也是许多药物的重要合成中间体。此外，硫醚及其衍生物也是合成含硫官能团化合物的重要构建板块。然而，合成此类化合物的方法大多涉及昂贵的过渡金属催化剂的使用、较长的反应时间、苛刻的反应条件以及对环境产生过度金属污染等局限性。因此，发展一种新型的、高效的绿色合成含硫化合物的方法具有重要的意义。到目前为止还未见运用微波辅助方法合成硫醚化合物的报道。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t该成果提出了利用微波辅助的方法合成通式(R1S)nR2中表达的硫醚类化合物。式中：R1为烷基、苯基、对甲基苯基、对氯苯基、对甲氧基苯基或对溴苯基；R2为次甲基、亚甲基、烷基或环烷基；n \\u003d 1，2或3。该方法反应条件温和，操作简便、反应时间短、反应收率高，同时反应中不适用金属催化剂，符合绿色化学的理念。\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18726473374\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":316,\"img\":\"/img/2019-12-27/c2897c578b3846ffbfdc5b0f81d31cbf.png\"},{\"oId\":316,\"img\":\"\"},{\"oId\":316,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '8', '192.168.30.29', '2019-12-27 12:27:32');
INSERT INTO `sys_log` VALUES ('1400', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":317,\"userName\":\"ceshi01\",\"resultTechnolo\":\"基于沉积物铁循环调控的地下水修复技术\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"新能源与节能\",\"industryDetail\":\"可再生清洁能源\",\"attribute\":\"独立完成\",\"patenNname\":\"基于沉积物铁循环调控的地下水修复技术\",\"patenNumber\":\"18276487234.01\",\"price\":80,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t本技术旨在解决地下水有机物污染。利用自然沉积物中含有的铁矿物及天然配体分子，通过天然配体调控沉积物表面铁循环，提高氧化剂利用效率。本技术通过原位注入井，将天然配体和双氧水混合液注入到被污染的含水层，验证这一方法用于有机污染物原位化学氧化的高效性。适合多种类型有机污染地下水治理，配体随污染物一起降解。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t本技术通过原位注入井，将天然配体和双氧水混合液注入到被污染的含水层，验证这一方法用于有机污染物原位化学氧化的高效性。适合多种类型有机污染地下水治理，配体随污染物一起降解。\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18726437364\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":317,\"img\":\"/img/2019-12-27/8d3fec18bea848f8b5f36dcdf77b4108.png\"},{\"oId\":317,\"img\":\"\"},{\"oId\":317,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '8', '192.168.30.29', '2019-12-27 12:28:50');
INSERT INTO `sys_log` VALUES ('1401', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":318,\"userName\":\"ceshi01\",\"resultTechnolo\":\"基于北斗/GNSS的矿区边坡变形在线监测关键技术\",\"stage\":\"已有样品\",\"advantages\":\"软件、理论著作\",\"industry\":\"航空航天\",\"industryDetail\":\"航天\",\"attribute\":\"独立完成\",\"patenNname\":\"基于北斗/GNSS的矿区边坡变形在线监测关键技术\",\"patenNumber\":\"1278674658.90\",\"price\":300,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t随着我国基础工程建设的快速投入，在施工工程量大、技术难度高和工期紧凑等多重因素影响下，大型缆机因对地形复杂、难以通行的大型施工现场具有良好的适用性，已经在大型水电站、超高建筑物和大型桥梁等施工现场得到广泛应用。但是，由于施工现场干扰因素众多，给施工安全带来了更为严峻的挑战，对施工安全管理水平提出了更高要求。传统大型缆机导航和防撞，主要以人工监测指挥和编码器机械定位两种方式为主，但由于工作量大、效率低以及人员安全难以保证，无法满足当前重大工程的信息化、智能化的全天候施工安全监测需求。本项目针对大型缆机在施工工程中安全高效运行的控制需求，研发出基于GIS的大型缆机智能诱导与防撞的业务化软件平台，具有实时性、连续性、自动化、高精度等特点，软件系统功能齐全、实用性强，单次报警时间小于0.1秒，漏警率不超过百万分之三。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t项目成果在大型缆机的组合导航定位算法、诱导防撞模型和应用系统研发等关键技术方面取得重大突破，对提高信息化施工领域的应用水平有着重大实质推动作用。项目成果已经在大岗山水电站、溪洛渡水电站、锦屏水电站、亭子口水电站、云南观音岩水电站和西藏藏木水电站等国家十二五重点工程中得到应用，为工程项目建设节约开支累计达3.7亿元，在优化施工进度、提高施工机械利用率以及控制施工质量和成本等方面发挥了典型示范作用，社会经济效益显著。经过湖北省科学技术厅组织专家委员会鉴定，一致认为该成果整体达到国际领先水平。\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18736473647\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":318,\"img\":\"/img/2019-12-27/759a729e690745a19a6564940e187a97.png\"},{\"oId\":318,\"img\":\"\"},{\"oId\":318,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '10', '192.168.30.29', '2019-12-27 12:30:58');
INSERT INTO `sys_log` VALUES ('1402', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":319,\"userName\":\"ceshi01\",\"resultTechnolo\":\"北斗卫星导航民用核心模块开发\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"航空航天\",\"industryDetail\":\"航空\",\"attribute\":\"企业合作\",\"patenNname\":\"北斗卫星导航民用核心模块开发\",\"patenNumber\":\"12376782736.08\",\"price\":400,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t卫星导航定位技术在经济社会发展中的应用越来越广泛，从基础测绘、工程勘察、数字城市、数字区域建设到资源调查、国土规划、交通运输、地震监测、公共安全与应急管理等众多领域，成为继蜂窝移动通信和互联网之后的第三大丌经济新增长点。北斗卫星导航民用芯片模块开发项目在国家卫星导航信息服务中心指导下，以军民融合、军管民用、资源共享为宗旨，以“中国北斗卫星导航系统服务平台”为应用推广平台，通过自主可控新—代芯片技术和信息安全技术，开发生产高性能、低成本的北斗导航民用芯片模块及终端系列产品，填补国产实名制北斗民用芯片模块产业化的空白，加快实现对国外卫星导航产品的替代，保障国家信息安全，改变国家信息产业格局，推动国家战略|生产业发展\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t北斗卫星导航民用芯片模块开发项目在国家卫星导航信息服务中心指导下，以军民融合、军管民用、资源共享为宗旨，以“中国北斗卫星导航系统服务平台”为应用推广平台，通过自主可控新—代芯片技术和信息安全技术，开发生产高性能、低成本的北斗导航民用芯片模块及终端系列产品，填补国产实名制北斗民用芯片模块产业化的空白，加快实现对国外卫星导航产品的替代，保障国家信息安全，改变国家信息产业格局，推动国家战略|生产业发展\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18726473647\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":319,\"img\":\"/img/2019-12-27/625d13dba812488e8069b8d2b9c538c6.png\"},{\"oId\":319,\"img\":\"\"},{\"oId\":319,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '6', '192.168.30.29', '2019-12-27 12:32:15');
INSERT INTO `sys_log` VALUES ('1403', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":320,\"userName\":\"ceshi01\",\"resultTechnolo\":\"嵌入式移动WEB服务器\",\"stage\":\"试验阶段\",\"advantages\":\"发明专利\",\"industry\":\"电子信息\",\"industryDetail\":\"信息安全技术\",\"attribute\":\"独立完成\",\"patenNname\":\"嵌入式移动WEB服务器\",\"patenNumber\":\"123876587.98\",\"price\":500,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\t该成果运用嵌入式系统构成一个WEB服务器，将企业互联网域名和动态IP地址绑定，可以在任何时间、任何地方，只要能接入互联网，就可以和外界交互信息。设备耗电量小，安全性高，运行成本和维护费用低，广泛用于中小型企业发布信息。\\u003c/p\\u003e\\u003cp\\u003e该;\\u003c/p\\u003e\\u003cp\\u003e\\t\\t主要技术指标：1、CPU：频率200MHz；2、整机功耗：≤5W；3、存储器：2Gb；4、并发请求数：≥5；5、请求响应时间：≤0.1s；7、通讯速率：512Kbs-2Mbps\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t产品技术处于国际先进水平，在互联网信息采集、传输和处理的设备研究领域具有重要的地位，其先进性在于将互联网动态IP地址资源有效地利用在企业网站运行，有效避免了网络拥塞。申请专利的竞争对手是日本松下公司，目前已经获得国家发明专利授权。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672637\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":320,\"img\":\"/img/2019-12-27/eeecefb019ce48a5aa7f73d47a60dee1.png\"},{\"oId\":320,\"img\":\"\"},{\"oId\":320,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '8', '192.168.30.29', '2019-12-27 12:35:01');
INSERT INTO `sys_log` VALUES ('1404', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":321,\"userName\":\"ceshi01\",\"resultTechnolo\":\"深空探测航天器轨道控制参数演化优化仿真软件\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"航空航天\",\"industryDetail\":\"航天\",\"attribute\":\"独立完成\",\"patenNname\":\"深空探测航天器轨道控制参数演化优化仿真软件\",\"patenNumber\":\"12682435.0\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\t深空探测航天器轨道控制参数演化优化仿真软件包括：初始轨道、 引力辅助轨道及精确轨道设计，基于 XML 的任务管理，探测任务三维仿 真。软件特点：实现了三种初始轨道设计模型：双脉冲模型、圆锥曲线 拼接模型以及重叠圆锥曲线拼接模型。采用了多种优化算法对初始轨道 及引力辅助轨道控制参数进行优化。考虑了第三体引力、非球形引力、 大气阻力、太阳光压等摄动对探测器的影响，实现了打靶法和基于 B 平 面的微分修正法等精确轨道设计方法。实现了多种星历模型，包括 DE405 和 JPL 等模型。实现了星空背景以及探测任务的三维仿真。\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t采用 MVC 三 层架构模式对软件进行设计，实现了界面层与逻辑层的解耦，软件具有 较好的可扩展性、灵活性和可重用性。 该软件计算精度已达到工程任务设计精度要求。 该软件平台已在 863 重大专项、地球-火星任务设计预研等重大工程 中得到应用与检验，在深空任务优化设计方面具有广泛的应用前景。\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18727382738\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":321,\"img\":\"/img/2019-12-27/be7dd1f7d0ab4a6694bbeafd36891a61.png\"},{\"oId\":321,\"img\":\"\"},{\"oId\":321,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '8', '192.168.30.29', '2019-12-27 12:36:51');
INSERT INTO `sys_log` VALUES ('1405', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":322,\"userName\":\"ceshi01\",\"resultTechnolo\":\"来源于日本的污泥处理和土壤修复处理技术\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"固体废弃物处置与综合利用技术\",\"attribute\":\"独立完成\",\"patenNname\":\"来源于日本的污泥处理和土壤修复处理技术\",\"patenNumber\":\"128376871.01\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e日本一家公司研发的土壤改良剂技术的主要成分是镁系纯天然无极矿物质，经过一系列复杂的化学和物理反应、改变污泥性质、利用结晶水转移原理将污泥内部水强制析出至污泥表面、从而达到降低含水率、实现污泥减量 的目的。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e此技术具有低成本、高效、不产生二次污染等特性，可实现污泥有处理的减量化、无害化、资源化处理目标，将有机物分解成无害物质。除此之外，处理后可用于园林绿化用土、农业用土、有机肥料土、筑路材料等多种目的。该技术自 2003 年在日本已经开始商业运行，至今已超过 200 例。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18737481728\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":322,\"img\":\"/img/2019-12-27/b378dfe8fb9644f4a16e8f08dd884942.png\"},{\"oId\":322,\"img\":\"\"},{\"oId\":322,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '9', '192.168.30.29', '2019-12-27 12:38:29');
INSERT INTO `sys_log` VALUES ('1406', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":323,\"userName\":\"ceshi01\",\"resultTechnolo\":\"常温低浓度气体污染物净化材料\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"大气污染控制技术\",\"attribute\":\"独立完成\",\"patenNname\":\"常温低浓度气体污染物净化材料\",\"patenNumber\":\"1237678436.0\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e用于半封闭空间低，浓度气体污染物的净化处理，原料简单易得，制作工艺简单，浆料稳定且在整体式催化剂涂覆牢固，材料耐水性好，不使用贵金属成本低实现在常温、低浓度（小于10ppm）和空速不小于10000h -1 条件下，400小时寿命考察对NOx的平均净化效率达70%以上。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t当前\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e发展了至少2种有实际应用前景的NO吸附富集及催化净化材料；成功用于停车场、公路隧道等工程示范装置相关污染物处理。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18726372738\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":323,\"img\":\"/img/2019-12-27/dd32da3f78fd443e915b7e32ec95734c.png\"},{\"oId\":323,\"img\":\"\"},{\"oId\":323,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '11', '192.168.30.29', '2019-12-27 12:39:59');
INSERT INTO `sys_log` VALUES ('1407', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":324,\"userName\":\"ceshi01\",\"resultTechnolo\":\"高温超导强磁分离选矿设备\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"资源勘查、高效开采与综合利用技术\",\"attribute\":\"院企合作\",\"patenNname\":\"高温超导强磁分离选矿设备\",\"patenNumber\":\"1238967324.23\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e课题来源于中国科技部2015年国家国际合作专项项目《高温超导磁分离装置合作研发》（2015DFR00120），研究起始于2015年4月，研究终止于2018年3月。拥有独立自主研发能力，相关实用专利2项：《一种直接制冷式高温超导磁选装置》（ZL2018205419871）、《一种高温超导磁体线圈用杜瓦装置》（ZL2017215430105）\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e高温超导磁选机是采用高温超导带材替代原有的常导铜线圈制成磁选机磁体，由于高温超导线材的高载流能力和低能耗特性，提高了磁选机选矿能力，同时降低磁选机能耗，产生3.5T高梯度磁场，极大地增强弱磁性矿处理范围。 \\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"五年独占许可转\",\"linkMan\":\"li\",\"phoneNumber\":\"18723728378\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":324,\"img\":\"/img/2019-12-27/6b3ae8bcdc2a4956b68e1174f8689d6b.png\"},{\"oId\":324,\"img\":\"\"},{\"oId\":324,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '10', '192.168.30.29', '2019-12-27 12:41:06');
INSERT INTO `sys_log` VALUES ('1408', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":325,\"userName\":\"ceshi01\",\"resultTechnolo\":\"河流生态修复技术工具箱\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"水污染控制与水资源利用技术\",\"attribute\":\"独立完成\",\"patenNname\":\"河流生态修复技术工具箱\",\"patenNumber\":\"123987235.98\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e河流生态修复技术工具箱键技术研究是基于水利部公益性行业专项《水生态文明建设关键技术研究与示范》（201401003）和水利部公益性行业专项《河湖水系生态连通规划关键技术研究与示范》（201501030）项目资助下展开的。 主要包括土工材料复合生态护岸技术、植生式挡土墙生态护岸技术、生态减渗技术、仿生式多组合生态净水堰技术、多单元岸坡型湿地水质净化系统和砂石坑改造为景观型净水调蓄池等技术：（1）土工材料复合生态护岸技术。采用工程与植被相结合的护岸型式，包括护脚结构、设于护脚结构上方的多层生态袋层及用于固定生态袋层的固定结构；（2）植生式挡土墙生态护岸技术。利用天然建筑材料及墙面植被层打造生态挡土墙结构，包括水体岸坡和设于水体岸坡的临水面的基础挡土墙；（3）生态减渗技术。利用天然弱渗透材料对河床底质重构，包括减渗层、生态层和保护层，形成净水减渗河床；\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e分借鉴了国内外前沿科研成果与工程实践，吸收了相关领域最新技术方法，构建了包括河流纵向、横向、垂向的河流生态修复三维措施体系，形成“河流廊道全覆盖、纵向-横向-垂向三维立体式”河流生态修复技术工具箱，具有技术方法先进性。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"18723673627\",\"phoneNumber\":\"19827382378\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":325,\"img\":\"/img/2019-12-27/77bef2b4416f45a2a9a203a935028813.png\"},{\"oId\":325,\"img\":\"\"},{\"oId\":325,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '8', '192.168.30.29', '2019-12-27 12:42:28');
INSERT INTO `sys_log` VALUES ('1409', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":326,\"userName\":\"ceshi01\",\"resultTechnolo\":\"核安防护特种涂装用高新能稀土基靶材及涂料\",\"stage\":\"试验阶段\",\"advantages\":\"实用新型专利\",\"industry\":\"高技术服务\",\"industryDetail\":\"高技术专业化服务\",\"attribute\":\"独立完成\",\"patenNname\":\"核安防护特种涂装用高新能稀土基靶材及涂料\",\"patenNumber\":\"12378628756.23\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e本项目开发的新型稀土基特种涂装材料[ Ga-（RE）-Zr--M ]（靶材及涂料），其主要应用于配套核燃料/乏燃料运输及储存过程中相关容器及装备表面耐温抗中子等辐解等核电核安的特种需求领域。其中经新开发的稀土基涂装材料涂装后的板材其耐温性可提高到1200度以上，抗中子等核辐解的时间提高3-7倍！\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e本项目开发的新型稀土基特种涂装材料[ Ga-（RE）-Zr--M ]（靶材及涂料），其主要应用于配套核燃料/乏燃料运输及储存过程中相关容器及装备表面耐温抗中子等辐解等核电核安的特种需求领域。其中经新开发的稀土基涂装材料涂装后的板材其耐温性可提高到1200度以上，抗中子等核辐解的时间提高3-7倍！\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18738737267\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":326,\"img\":\"/img/2019-12-27/ce5759ad0b0d4e26a7726387c13bcf27.png\"},{\"oId\":326,\"img\":\"\"},{\"oId\":326,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '10', '192.168.30.29', '2019-12-27 12:44:16');
INSERT INTO `sys_log` VALUES ('1410', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":327,\"userName\":\"ceshi01\",\"resultTechnolo\":\"基底金属化多层薄膜及其图案化制备技术\",\"stage\":\"试验阶段\",\"advantages\":\"实用新型专利\",\"industry\":\"先进制造与自动化\",\"industryDetail\":\"安全生产技术\",\"attribute\":\"企业合作\",\"patenNname\":\"基底金属化多层薄膜及其图案化制备技术\",\"patenNumber\":\"21378675.34\",\"price\":400,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e创新性（重点阐明如何区别于传统技术，专利等知识产权状况） (1)可作为高导电和高粘附力金属材料用于微型电子器件中的薄膜电极使用。 (2)可作为陶瓷基底与其它材料之间的过渡缓冲层，利用高粘附及优良的力学性能获得具有长周期高可靠的多层薄膜。可用于后续焊接工艺与金属引线等。 (3)实现金属薄膜周期排布以及微型图案化，在1μm~30mm陶瓷基底范围内获得各种表面功能的金属化图案薄膜 独占性（重点阐明技术上是否难以获取或复制） 本技术在多层异质金属薄膜制备工艺、基底表面处理工艺，以及激光图案化兼容性加工技术方面均有独创性，在技术复制上存在一定难度。 盈利性（重点阐明是否带来成本下降或性能提高） 利用简单可控的溅射技术，实现了AlN等陶瓷的薄膜金属化，Ti-Cu/Ti 作为Cu与AlN陶瓷基底的过渡层，降低了膜的电阻率，改善了结合力，增强了可靠性，制膜工艺简单快速，因此本技术综合成本可以进一步降低，效益可观。与激光技术配合，获得了复杂的金属薄膜周期排布与电路图案，此技术具有广阔的应用前景。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e持续性（重点阐明技术储备和持续创新能力） 柔性电子需求的增加极大地促进了柔性电极的发展，目前报道出的柔性电极大多存在成本高、工艺复杂、导电性不理想、可靠性差等问题。该技术通过持续创新，可实现柔性薄膜电极在服役环境中保持电学性能的稳定，同时薄膜电极与基底结合强度高、制备工艺简单、原材料成本低廉，是拓展为一种大规模生产柔性薄膜电极的关键技术。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"liu\",\"phoneNumber\":\"19827382738\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":327,\"img\":\"/img/2019-12-27/ae674d1ddc0b43b9a9d0940d441d1487.png\"},{\"oId\":327,\"img\":\"\"},{\"oId\":327,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '7', '192.168.30.29', '2019-12-27 12:46:16');
INSERT INTO `sys_log` VALUES ('1411', 'ceshi01', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":328,\"userName\":\"ceshi01\",\"resultTechnolo\":\"北斗微基站应急救援系统\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"电子信息\",\"industryDetail\":\"新型电子元器件\",\"attribute\":\"独立完成\",\"patenNname\":\"北斗微基站应急救援系统\",\"patenNumber\":\"1237576234.03\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e“北斗微基站应急救援系统”是基于北斗RDSS/RNSS双模微基站组网构建的广域位置报告与应急救援指挥控制系统。该项目目标是针对突发灾害形势下，面向解决广域范围复杂地理环境条件下，空地协同救援系统运行过程中的通信盲区应急位置报告与组网、基于位置的实时大容量并发信息服务、复杂协同救援任务规划与管理等关键科学问题。该项目来源于自主研发经费，2013年项目启动，2016年投入应用并持续改进。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e北斗微基站应急救援系统可以同步支持专业救援机构以及民用普通用户使用，系统设计了低成本、集成度高、使用便捷并具有三防能力的北斗微基站面向用户提供服务，相比于传统的救援终端，在成本、体积重量以及使用便利性方面具有显著优势，可以装备于单人/车辆/船只/飞机等各类载具。 在服务能力方面，本系统可以应用于地震/水灾/泥石流/森林防火灾害、空难/海上搜救、野外探险等各类活动，面向用户提供全域通信网络接入、位置报告/文字/语音短报文传输、救援态势感知、救援任务规划决策辅助等服务，相比其他系统在服务类型与救援支持能力方面优势明显。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"李\",\"phoneNumber\":\"15627362738\",\"province\":\"天津市\",\"city\":\"市辖区\",\"district\":\"河东区\",\"imgAddress\":[{\"oId\":328,\"img\":\"/img/2019-12-27/85fada0623a54bf586b043376e23efc7.png\"},{\"oId\":328,\"img\":\"\"},{\"oId\":328,\"img\":\"\"}],\"companyName\":\"中科淮北\"}]', '9', '192.168.30.29', '2019-12-27 13:38:53');
INSERT INTO `sys_log` VALUES ('1412', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":329,\"userName\":\"ceshi02\",\"resultTechnolo\":\"图书共享服务平台中的“书易得 •智能书柜 ”\",\"stage\":\"试验阶段\",\"advantages\":\"实用新型专利\",\"industry\":\"电子信息\",\"industryDetail\":\"计算机产品及其网络应用技术\",\"attribute\":\"独立完成\",\"patenNname\":\"图书共享服务平台中的“书易得 •智能书柜 ”\",\"patenNumber\":\"12736575.12\",\"price\":300,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e书易得 •智能书柜是 “图书共享服务平台”项目的线下终端产品：包含书柜设备接口程序和书柜客户界面程序。书柜的操作者是运营人员和公众读者。公众读者在书柜前完成借书、还书、续借、捐赠、图书销售（借助平台）等操作。平台可支持多种借书方式：语音借书、人脸识别借书、扫二维码借书、图书证借书等。智能书柜可任意扩展书架，每个书柜有唯一标识和地理位置，同时书柜可定制书格数量、布局、显示设备等。书柜占地面积小，可以在办公场所、社区、车站、广场、商场、机场等任何地方摆放。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e该产品2018年获得国际AI大赛优秀奖。 图书共享服务平台是 “书易得 •智能书柜 ”的功能管理系统，为满足读者日常阅读行为，便捷获取图书借阅、共享，随时随地免费获取精准优质知识阅读内容而研发设计的。前后端分离，服务后端部署在互联网云端，所有的业务数据逻辑处理均在此平台完成。主要包含：图书服务、用户服务、书柜服务、借还服务。管理后台端：管理后台端的用户主要是系统管理员、平台运营人员、平台内的文化机构管理员。系统管理员维护平台内的所有用户、系统参数配置、数据字典、菜单权限等。平台运营人员负责运营发布配置平台的微应用的软文内容、图书推荐、节假日活动配置等运营功能。平台内的文化机构管理员负责维护本机构分享出的图书信息，关注这些图书信息的流通记录及查询图书历史流通情况等。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"五年独占许可转\",\"linkMan\":\"li\",\"phoneNumber\":\"18726372738\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":329,\"img\":\"/img/2019-12-27/aca14d737aa34604b19ee1ba92762194.png\"},{\"oId\":329,\"img\":\"\"},{\"oId\":329,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '11', '192.168.30.29', '2019-12-27 13:40:30');
INSERT INTO `sys_log` VALUES ('1413', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":330,\"userName\":\"ceshi02\",\"resultTechnolo\":\"脓毒症的发病机制及集束化治疗\",\"stage\":\"试验阶段\",\"advantages\":\"发明专利\",\"industry\":\"生物与新医药\",\"industryDetail\":\"医药生物技术\",\"attribute\":\"独立完成\",\"patenNname\":\"脓毒症的发病机制及集束化治疗\",\"patenNumber\":\"123876123.12\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e脓毒症是宿主对感染的反应失调引起的威胁生命的器官功能障碍，病死率达25%以上，存活的患者中约有15%存在认知功能障碍，严重影响人类健康。目前脓毒症仍缺乏有效的治疗手段，因此寻找有效的脓毒症治疗方法是临床亟待解决的关键问题。基于上述原因，我们从脓毒症的发病机制及临床治疗两方面开展研究。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e目前干细胞的来源选择多种多样，我们选择脂肪来源干细胞（adipose-derived stem cells, ADSC）。因ADSC取材于脂肪组织，来源广泛，分离培养技术已经很成熟，同时不用考虑伦理问题。 目前国外关于β受体阻滞剂对脓毒性休克疗效的临床研究较少，且样本量小，质量低。其中最值得注意的是意大利罗马大学团队的研究，他们关于此课题进行了预实验及II期临床试验（RCT），结果发现能降低脓毒性休克患者心率，且无不良反应，短期随访发现β受体阻滞剂能显著降低病死率。而我国此类研究很少（只有1例RCT）且存在明显不足，样本量较少，不能进行良好的质控，因此亟需大样本、高质量的随机对照试验来进一步探究并验证β受体阻滞剂对脓毒性休克患者预后的影响\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672637\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":330,\"img\":\"/img/2019-12-27/b39d44f40aa247c8aec72d9c3a1fe4e3.png\"},{\"oId\":330,\"img\":\"\"},{\"oId\":330,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '5', '192.168.30.29', '2019-12-27 13:42:24');
INSERT INTO `sys_log` VALUES ('1414', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":331,\"userName\":\"ceshi02\",\"resultTechnolo\":\"航天用轻合金表面功能化与防护关键技术 及应用\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"航空航天\",\"industryDetail\":\"航天\",\"attribute\":\"独立完成\",\"patenNname\":\"航天用轻合金表面功能化与防护关键技术 及应用\",\"patenNumber\":\"123876123.12\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e针镁合金、钛合金等轻金属综合性能优异，有助于降低卫星结构 重量，提升有效载荷的比重，被广泛应用于航天领域。但对于镁合金 而言，耐蚀性能较低，在材料加工，装配，地面测试等过程中极易发 生腐蚀，造成零件力学性能、导电性能下降，进而影响型号产品寿命 与可靠性。此外，航天飞行器在空间运行时，经受的温差变化较大， 需要对其进行主动、被动热控设计，被动热控设计要求结构件表面防 护层具有一定的太阳吸收率与辐射率，从而达到温度平衡。因此，就 要求对镁合金零件进行表面处理，使其能够有效能抵御腐蚀或达到热 控性能，以避免由于腐蚀、辐照导致的卫星性能下降甚至报废等不良 后果。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e主要研究了 钛合金、镁及镁锂合金等航天器用阀金属的表面耐蚀、强化、耐磨以 及热控膜层的制备工艺技术，并阐述了相应的防护、强化机理，为航 天飞行器用轻合金的表面防护与强化技术应用奠定了基础。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18726372738\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":331,\"img\":\"/img/2019-12-27/bc1e16c016334e6591b9e0504dd5cb45.png\"},{\"oId\":331,\"img\":\"\"},{\"oId\":331,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '6', '192.168.30.29', '2019-12-27 13:44:00');
INSERT INTO `sys_log` VALUES ('1415', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":332,\"userName\":\"ceshi02\",\"resultTechnolo\":\"水利水电三维地理信息云服务平台\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"航空航天\",\"industryDetail\":\"航空\",\"attribute\":\"企业合作\",\"patenNname\":\"水利水电三维地理信息云服务平台\",\"patenNumber\":\"2132423235.10\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e\\u0026nbsp;水利水电行业是一个需要在大尺度地理空间上综合运用海量信息开展工作的行业，将各类水利信息集成到地理空间模型上，建立流域乃至全国的水利地理信息模型，并通过三维地理信息平台实现有效的管理。水利水电三维地理信息云服务平台的开发基于上述背景产生的计划外自选任务。建立水利三维地理信息云服务平台是实现水利水电全面升级的重要基础工作；是实现水利信息化的关键步骤之一；同时填补了国内目前尚无适应于水利水电各专业的三维地理信息系统平台的空白。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e综合运用了地理信息系统、三维虚拟现实、摄影测量与遥感、空间数据库、计算机网络、并行计算等现代高新技术手段，构建了一个拥有自主知识产权、系统功能满足水利行业设计与应用要求的水利水电三维地理信息云服务平台3DGIS-Ark。研发了三维地形地貌生成，三维模型数据处理，三维数据库加解密，三维地形编辑，三维地理信息集成、浏览与分析子系统等子系统，实现了水利水电空间信息和专业信息的集成管理、三维可视化与分析。 3DGIS-Ark软件平台创新性地解决了复杂水利水电工程枢纽建筑物与三维地形的无缝智能镶嵌，宏大地理场景下的地上下、室内外一体化数据集成管理与可视化，三维GIS环境下原因量与效益量动态变化关系表达等难点问题\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18726372837\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":332,\"img\":\"/img/2019-12-27/16069e4cf3f94e2c8ea519202ce2f9cf.png\"},{\"oId\":332,\"img\":\"\"},{\"oId\":332,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '8', '192.168.30.29', '2019-12-27 13:45:19');
INSERT INTO `sys_log` VALUES ('1416', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":333,\"userName\":\"ceshi02\",\"resultTechnolo\":\"甲板及船壳热反射漆的制备及应用\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"新材料\",\"industryDetail\":\"无机非金属材料\",\"attribute\":\"企业合作\",\"patenNname\":\"甲板及船壳热反射漆的制备及应用\",\"patenNumber\":\"122313987.23\",\"price\":300,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e太阳热反射涂层就是对太阳的热辐射具有高反射率的涂层，可显著降低装备表面的温度，提高使用者的舒适度和减少降温用能耗。自上世纪七十年代以来，随着现代工业及技术的发展，要求使用反射太阳能的新型涂料，以降低暴露在太阳辐射热下的装备表面涂层涂层温度，从而阻止热能传导，达到改善工作环境，提高安全性等目的。例如建筑业的屋顶和玻璃幕墙、石油工业的海上钻井平台、油罐、石油管道、运输业的汽车、火车、飞机表面、造船工业的船壳、甲板，以及兵器及航天工业的坦克、军舰、火箭等方面均有热反射涂层的需求。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e随着造船业的迅速发展，对具有较好的防腐、防污、自清洁和热反射功能的甲板漆和船壳漆的需求将越来越大。。 我们课题组发展制备了具有热反射功能的可用于船舶甲板和船壳部位的热反射涂层性能优异，其热反射率达到75%以上，可使被涂覆装备的表面温度显著降低，可广泛应用与民用船舶及军舰甲板及船壳表面的防护和热反射降温。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"五年独占许可转\",\"linkMan\":\"li\",\"phoneNumber\":\"19827382738\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":333,\"img\":\"/img/2019-12-27/d131e7d8c3434e47bea3bd6a24926153.png\"},{\"oId\":333,\"img\":\"\"},{\"oId\":333,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '5', '192.168.30.29', '2019-12-27 13:47:24');
INSERT INTO `sys_log` VALUES ('1417', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":334,\"userName\":\"ceshi02\",\"resultTechnolo\":\"气相合成亚微米级超细、功能性粉体材料\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"新材料\",\"industryDetail\":\"高分子材料\",\"attribute\":\"独立完成\",\"patenNname\":\"气相合成亚微米级超细、功能性粉体材料\",\"patenNumber\":\"3242347234.45\",\"price\":340,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e该项目利用等离子态时物质所处的高激发、高活性、高能密度的特点，采用不同气氛和反应物在高温下氧化热分解、气化以及”骤冷”等条件，使产品”冻结”在一特殊状态，制备出超细氧化锆、钠米级氧化锌、氧化钴及氧化镁等产品，高频等离子体装置可连续稳定运行。 超微细氧化锆粉体的产品工艺不仅可以填补国内空白，而且对我国新材料科学等领域发展具有深远的影响，可以使陶瓷行业的科技水平有长足的进步，极大地促进相关产品及产业的发展。高纯度、超微细、功能性粉体材料已成为现代工业发展所必须，而常规的冶金工艺和材料制备技术在某些方面却难以胜任，等离子体具有高温、高能量密度和气氛可控的特点，特别适合于制备与合成锆金属、锆金属化合物与复合超细粉末。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e超微细氧化锆粉体的成功工业化生产，产品工艺不仅可以填补国内空白，而且对我国新材料科学等领域发展具有深远的影响，可以使本地区陶瓷行业的科技水平有长足的进步，极大地促进相关产品及产业的发展。高纯度、超微细、功能性粉体材料已成为现代工业发展所必须，而常规的冶金工艺和材料制备技术在某些方面却难以胜任。有广阔 的市场应用前景。 氧化锆粒径在0.14～0.2μm之间，晶型（四方型+立方型）\\u0026gt;80% ，ZrOz纯度\\u0026gt;99%，其它三种产品粒径均在35～60nm之间。如果按100吨（氧化锆）/年规模计，可创产值约2000元左右/年。 与云南省冶金研究院合作, 2001年通过云南省成果鉴定。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672637\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":334,\"img\":\"/img/2019-12-27/87a1d4f6e885436bbfe6ede543481d3f.png\"},{\"oId\":334,\"img\":\"\"},{\"oId\":334,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '10', '192.168.30.29', '2019-12-27 13:48:25');
INSERT INTO `sys_log` VALUES ('1418', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":335,\"userName\":\"ceshi02\",\"resultTechnolo\":\"漂浮式水上光伏发电系统\",\"stage\":\"可以量产\",\"advantages\":\"实用新型专利\",\"industry\":\"新能源与节能\",\"industryDetail\":\"可再生清洁能源\",\"attribute\":\"独立完成\",\"patenNname\":\"漂浮式水上光伏发电系统\",\"patenNumber\":\"2343235243.23\",\"price\":550,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e\\u0026nbsp;漂浮式水上光伏将光伏电站建在采煤沉陷区、工业水池、水库、河道等水域，节约宝贵的土地资源，拓展光伏的利用空间，又能够减少水体蒸发和藻类繁殖，和养殖、旅游等结合实现水体综合利用，同时水的冷却作用还可大幅提高光伏电站发电量，提升光伏电站经济性。 在全球水上光伏刚刚起步之时，我司敏锐地把握到市场新动态，着手开展漂浮式水上光伏发电系统的研发工作。我司成立研发团队，投入上千万资金开展《水面光伏电站浮式基础研究》与《漂浮式水上光伏电站浮体及整体稳定性研究》等自主创新课题，系统化研究漂浮式水上光伏发电系统。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e新一代浮体产品——环境友好型插拔式水上光伏浮体打破常规设计思路，采用走道浮体浮力为主、连接浮体浮力为辅的形式；插拔式支架可保证光伏组件倾角的调节性；踏板浮体可提高电站整体透光率，环境友好；具有套管保护的双螺栓，可提高刚性螺栓与柔性浮体耳板间的兼容性，冗余设置提高抗拉力，从而保证系统安全；每一个浮体构件均为横平竖直，使得壁厚均匀，安全可靠，同时经济性更优。 我司研发的锚固系统——大型柔性水面漂浮方阵锚固系统以水动力、空气动力及结构动力学理论计算为依据，设计的浮体应力分散装置能够平均浮体受力，避免了应力集中，保证浮体完整性和有效性；嵌入式强吸附力锚块对水面漂浮系统进行整体稳定性控制；锚绳裕量最优化设计可将水面漂浮系统的垂直位移、水平位移以及偏转角度控制在允许范围内；差异化双重加密锚点共用技术，提高锚固系统稳定性与经济性。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723723834\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":335,\"img\":\"/img/2019-12-27/d1a3634570dd4b7aabc9a5cf41a9e9c6.png\"},{\"oId\":335,\"img\":\"\"},{\"oId\":335,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '9', '192.168.30.29', '2019-12-27 13:49:47');
INSERT INTO `sys_log` VALUES ('1419', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":336,\"userName\":\"ceshi02\",\"resultTechnolo\":\"基于外场强化的金属二次电池快速充电技\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"新能源与节能\",\"industryDetail\":\"高效节能技术\",\"attribute\":\"独立完成\",\"patenNname\":\"基于外场强化的金属二次电池快速充电技\",\"patenNumber\":\"3425347657.34\",\"price\":330,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e锂电池在充电过程中，锂离子在负极的非均匀沉积生成枝晶结构，导致电池短路，造成燃烧甚至爆炸等安全问题，因此枝晶问题是困扰锂电池行业发展的一个瓶颈问题。为了避免枝晶的生成，人们通常采用慢速充电的办法，用效率来换取安全，人们也曾尝试采用电极结构优化和电解液组成优化等方式，来解决锂枝晶问题，但是效果有限。\\u003c/span\\u003e\\u003c/p\\u003e\\u003cp\\u003e\\u003cspan class\\u003d\\\"ql-cursor\\\"\\u003e﻿\\u003c/span\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e如何在不改变电池材料和制造工艺条件下，解决枝晶问题，是人们长期来探索的科学问题。在前期的反应-传递调控材料研究中，中国科学院过程工程研究所介尺度研究部发现枝晶的生成是生长界面附近的浓度梯度或温度梯度导致的，通过消除界面梯度，可以抑制枝晶的生长。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e提出通过外场强化传质，来抑制锂电池枝晶，实验结果验证了这一方法的有效性，取得了如下创新性成果。 （1）在锂电池充电时，在负极外侧引入交流电场，即便是高电流充电条件下（2 mA/cm2），没有枝晶产生，电池寿命提高了五倍。 （2）在正负极外侧添加磁场，亦能抑制枝晶的产生。 （3）外场强化技术，不改变电池材料和结构，只是在充电时，引入外场。 （4）该方法可以在充电站上应用，或者在车载电池组周围添加附件。 \\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672837\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":336,\"img\":\"/img/2019-12-27/748de0c1623c452f84aeebcb6661145a.png\"},{\"oId\":336,\"img\":\"\"},{\"oId\":336,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '8', '192.168.30.29', '2019-12-27 13:51:17');
INSERT INTO `sys_log` VALUES ('1420', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":338,\"userName\":\"ceshi02\",\"resultTechnolo\":\"基于中线式尾矿筑坝法的尾矿坝建设及运行关键技术\",\"stage\":\"可以量产\",\"advantages\":\"发明专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"物理性污染防治技术\",\"attribute\":\"独立完成\",\"patenNname\":\"基于中线式尾矿筑坝法的尾矿坝建设及运行关键技术\",\"patenNumber\":\"123124325.45\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e中线式尾矿筑坝法（centerline embankment method）就是在初期坝轴线处用旋流分级粗砂冲积尾矿的筑坝方式。它不同于上游法和下游法，而是要求所有的尾矿进行两段旋流分级，二段底流粗砂用于堆坝，一、二段溢流排入库内。其筑坝方式为坝体外坡保持一定的坡度，粗砂坝体沿初期坝轴线位置垂直上升。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e中线式尾矿筑坝法（centerline embankment method）就是在初期坝轴线处用旋流分级粗砂冲积尾矿的筑坝方式。它不同于上游法和下游法，而是要求所有的尾矿进行两段旋流分级，二段底流粗砂用于堆坝，一、二段溢流排入库内。其筑坝方式为坝体外坡保持一定的坡度，粗砂坝体沿初期坝轴线位置垂直上升。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672637\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":338,\"img\":\"/img/2019-12-27/06b46027077f4dfab858156bc545e92e.png\"},{\"oId\":338,\"img\":\"\"},{\"oId\":338,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '8', '192.168.30.29', '2019-12-27 13:54:20');
INSERT INTO `sys_log` VALUES ('1421', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":339,\"userName\":\"ceshi02\",\"resultTechnolo\":\"多缸薄板成型液压机组\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"先进制造与自动化\",\"industryDetail\":\"工业生产过程控制系统\",\"attribute\":\"企业合作\",\"patenNname\":\"多缸薄板成型液压机组\",\"patenNumber\":\"12342534.43\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e创新性 1.阵列式油缸技术；使工作台面压印力分布均匀，实现超大投影面积和高精度的薄板成型； 2.全液压比例伺服驱动，液压系统压力、流量智能闭环控制技术，整机节能降耗明显。 3.梳型滑块和移动距离锁止技术，大大减小主工作油缸工作行程，缩短压印周期，提高压印效率，降低能耗。 4.框架板预应力复合的整体机身，解决多板叠加式预应力组合框架的预紧力及预紧顺序加载的难题。有效的保证了薄板压印精度≦±0.1mm的要求。 5.具有模拟显示、故障自诊断、安全保护系统，自动化程度高，操作简便，运行安全可靠。 独占性 主要体现在超级性上，巨型薄板成型液压机，是象征重工业实力的国宝级战略装备，世界上能研制的国家屈指可数。目前世界上能够研发万吨级以上薄板成型压机的国家，只有中国和德国。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e盈利性 300MN多缸薄板成型液压机组于2014年6月销售给兰州兰石换热设备有限责任公司，合同总价5096万元。机组投产后，机组载荷作用均匀，压制的薄板成型产品质量稳定，废品率低，生产效率高，经济效益十分明显。 持续性 我公司在300MN多缸薄板成型液压机组研发和客户使用基础上，积累成功经验，陆续开发了从40MN到250MN多缸薄板成型液压机组系列产品。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672738\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":339,\"img\":\"/img/2019-12-27/cafc2878fd8d44c581debd3a3a52634d.png\"},{\"oId\":339,\"img\":\"\"},{\"oId\":339,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '8', '192.168.30.29', '2019-12-27 13:55:53');
INSERT INTO `sys_log` VALUES ('1422', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":340,\"userName\":\"ceshi02\",\"resultTechnolo\":\"优质饮用水净化及安全保障技术与设备\",\"stage\":\"已有样品\",\"advantages\":\"实用新型专利\",\"industry\":\"先进制造与自动化\",\"industryDetail\":\"新型机械\",\"attribute\":\"独立完成\",\"patenNname\":\"优质饮用水净化及安全保障技术与设备\",\"patenNumber\":\"12432534.45\",\"price\":300,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e该技术融合了各种单元技术，可进行各类污染物质和微生物的高效去除，在获得高产水率的同时，使水中保持人体健康所需的微量元素；通过循环消毒系统中的净化消毒过程保持末端水的水质安全和生物稳定性，并运用在线监测与预警技术及时反映水质变化，可实现饮用水水质的风险控制和突发事件的及时发现与应对。系统具有良好的经济性、稳定性，采用的各单项技术均为成熟技术、运行管理简便、效果稳定。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e技术创新性： 1. 臭氧、紫外与在线臭氧组成了对微生物污染的三级安全屏障； 2. 纳滤膜有效地提高了系统产水率，系统产水率可高达85%以上； 3. 有效的水质口感调节系统设计，保留水体有益矿物质； 4. 独特的管网循环处理消毒系统设计，确保管网水质的鲜度和安全； 5. 整体工艺无任何加药系统，确保没有二次污染产生。 应用状况： 已成功服务2008北京奥运会，并在一些住宅小区、机关、学校、写字楼、宾馆饭店完成工程示范。 该技术拥有自主专利十余项，并获国家科技进步二等奖\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723782738\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":340,\"img\":\"/img/2019-12-27/88e17c6d4e154bd78d09ab6625da38df.png\"},{\"oId\":340,\"img\":\"\"},{\"oId\":340,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '10', '192.168.30.29', '2019-12-27 13:57:05');
INSERT INTO `sys_log` VALUES ('1423', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":341,\"userName\":\"ceshi02\",\"resultTechnolo\":\"基于滴滴轨迹大数据的 智慧信号灯系统\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"高技术服务\",\"industryDetail\":\"信息技术服务\",\"attribute\":\"独立完成\",\"patenNname\":\"基于滴滴轨迹大数据的 智慧信号灯系统\",\"patenNumber\":\"12343123412.12\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e城市化进程加快和汽车数量大幅度增加的背景下，全球城市交通面临前所未有的挑战。城市交通灯信号优化是最有效的缓解城市交通拥堵的手段之一，但在交通管理的理论和实践100多年的发展过程中，信号灯系统仍然依靠人工经验和固定点检测器数据进行配时优化。受人工成本和检测器维护成本的限制，信号优化周期通常要3-6个月，低优化频率导致低效的交通信号控制。传统信号灯控制和优化系统以固定点检测器数据为基础进行设计和建设，检测器布设有限，数据量特征不足，无法全景反映道路流量信息。当下的城市信号灯系统，交通控制系统一直缺乏一个连续、可靠、低成本的交通数据源。研发符合具有完全自主知识产权的基于大数据的智慧信号灯系统，高效地优化信号灯配时，对于改善城市交通效率、提升公众出行体验具有重要意义。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e移动互联网的高速发展近年来累积了大量移动出行数据，也为智慧交通系统的建设提供了一种新型交通大数据。利用轨迹数据进行交通状况估计和交通管理评估在近期成为研究和应用领域的热点之一。滴滴自主研发的基于滴滴轨迹大数据的智慧信号灯系统，是世界第一个利用轨迹数据对交通信号灯进行系统性分析优化并落地的智慧信号灯系统，从理论到实践都做了很多首创性的工作，是走在世界最前沿的探索和实践。\\u0026nbsp;\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18723736478\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":341,\"img\":\"/img/2019-12-27/3eaea7e92e37499fbde1186e1e017dc3.png\"},{\"oId\":341,\"img\":\"\"},{\"oId\":341,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '9', '192.168.30.29', '2019-12-27 13:59:10');
INSERT INTO `sys_log` VALUES ('1424', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":342,\"userName\":\"ceshi02\",\"resultTechnolo\":\"智慧工地\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"高技术服务\",\"industryDetail\":\"高技术专业化服务\",\"attribute\":\"独立完成\",\"patenNname\":\"智慧工地\",\"patenNumber\":\"1233423434.23\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e智慧工地是指运用信息化手段，通过三维设计平台对工程项目进行精确设计和施工模拟，围绕施工过程管理，建立互联协同、智能生产、科学管理的施工项目信息化生态圈，并将此数据在虚拟现实环境下与物联网采集到的工程信息进行数据挖掘分析，提供过程趋势预测及专家预案，实现工程施工可视化智能管理，以提高工程管理信息化水平，从而逐步实现绿色建造和生态建造。 智慧工地是一门综合性的学科集成，涉及板块内容较多，应用场景复杂，根据工地管理的要素分析，智慧工地的应用板块分为劳务管理、机械管理、材料管理、技术管理及生产管理。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e目前智慧工地的发展主要呈现分散化的特点，一是综合性技术集成能力不足，二是对于应用场景标准化解决方案缺失，三是信息化深度浮于表面。 本项目建设采用先进的物联网技术，主要由信息采集层、网络接入层、网络传输层、信息存储与处理层组成。 项目总体建设思路为：通过可视化、实名制系统为企业、现场工程管理，提供了先进技术手段。通过安装在施工作业现场的各类传感装置，构建智能监控和防范体系。从而为安全生产监督管理引入新理念，真正体现“安全第一、预防为主、综合治理”的安全生产方针；有效弥补传统方法和技术在监管中的缺陷；实现对人、机、料、法、环的全方位实时监控，变被动“监督”为主动“监控”。 技术创新点如下：1.可视化模块2.自动化监测模块3.设备监控模块4.人员实名制管理模块5.BIM轻量化模块6.项目信息化管理模块。 应用领域一是依据市政、房建、公路等不同工程项目的特点，打造综合性的智慧工地平台；二是将智慧工地各个板块的硬件或软件进行深度开发以满足复杂多变的工地环境需求；三是以工地所含的人、机、料等因素进行物联，将数据变成项目管理的重要依据，推进项目管理的效率。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"许可转让\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672637\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":342,\"img\":\"/img/2019-12-27/33bf592c8f224463bdc0afa8c2e0860c.png\"},{\"oId\":342,\"img\":\"\"},{\"oId\":342,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '5', '192.168.30.29', '2019-12-27 14:00:27');
INSERT INTO `sys_log` VALUES ('1425', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":343,\"userName\":\"ceshi02\",\"resultTechnolo\":\"地下工程软岩挤压大变形机理与控制关键技术\",\"stage\":\"试验阶段\",\"advantages\":\"实用新型专利\",\"industry\":\"高技术服务\",\"industryDetail\":\"高技术专业化服务\",\"attribute\":\"企业合作\",\"patenNname\":\"地下工程软岩挤压大变形机理与控制关键技术\",\"patenNumber\":\"1231231342.23\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e本项目覆盖土木建筑工程、水利工程和材料科学三大领域。随着我国交通、水利和能源等基础设施建设的发展，穿越高地应力地区且工程地质环境恶劣的长大隧道不断涌现，为我国地下工程建设带来新的机遇和挑战。项目组自2004年以来，在12项国家基础研究，10项科技攻关和数十项重大工程等项目资助下，形成高地应力软岩地下工程挤压大变形灾害的支护设计理论和控制技术。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e本项目覆盖土木建筑工程、水利工程和材料科学三大领域。随着我国交通、水利和能源等基础设施建设的发展，穿越高地应力地区且工程地质环境恶劣的长大隧道不断涌现，为我国地下工程建设带来新的机遇和挑战。项目组自2004年以来，在12项国家基础研究，10项科技攻关和数十项重大工程等项目资助下，形成高地应力软岩地下工程挤压大变形灾害的支护设计理论和控制技术。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672367\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":343,\"img\":\"/img/2019-12-27/b8b42c0ec18d4b21b992ebddc113f789.png\"},{\"oId\":343,\"img\":\"\"},{\"oId\":343,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '7', '192.168.30.29', '2019-12-27 14:01:27');
INSERT INTO `sys_log` VALUES ('1426', 'ceshi02', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":344,\"userName\":\"ceshi02\",\"resultTechnolo\":\"港口智能交通组织与调度系统平台\",\"stage\":\"已有样品\",\"advantages\":\"发明专利\",\"industry\":\"高技术服务\",\"industryDetail\":\"信息技术服务\",\"attribute\":\"独立完成\",\"patenNname\":\"港口智能交通组织与调度系统平台\",\"patenNumber\":\"12317263712.23\",\"price\":320,\"negotiable\":\"\",\"describe\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e随着水路运量的不断上升以及E-航战略执行计划的深入开展，船舶交通组织与服务的智能化已经成为水路交通研究领域的迫切需求。项目以实现整体交通最优化效益为目标，创新性的探索复杂港口水域船舶交通组织优化理论与方法。\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e\\t\\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e通过分析复杂水域船舶交通的动态特性，基于船舶交通组织所需的AIS、GPS、VTS 、ECDIS、VHF（Very High Frequency，甚高频）、CCTV（Closed Circuit Television，闭路监视系统）以及水文环境等数据建立港口智能交通组织数据库；研究各类型船舶交通数据的数据类型、数据特征以及动态数据转换和提取方法并构建关联分析图，基于通用海事数据结构构建数据格式标准、结构高效的船舶交通组织海事服务集并建立实时、动态的交通信息智能服务系统；协调航道利用率、泊位占用率和锚地使用率等影响因素，构建以通航模式转换策略、安全时距自适应调整策略以及交叉、会遇冲突消解策略为约束的复杂航道船舶交通协调组织优化模型；最终设计并实现港口智能交通组织与调度系统，项目研究对创新船舶交通组织与服务模式方法、丰富水上交通管理领域的相关理论、促进其与信息学科交叉融合等方面具有重要的理论与现实意义。\\u003c/span\\u003e\\u003c/p\\u003e\",\"transferType\":\"技术入股\",\"linkMan\":\"li\",\"phoneNumber\":\"18723672367\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":344,\"img\":\"/img/2019-12-27/ca18c7f1a68e4ea09ae84fbd74f7145b.png\"},{\"oId\":344,\"img\":\"\"},{\"oId\":344,\"img\":\"\"}],\"companyName\":\"中科智云\"}]', '7', '192.168.30.29', '2019-12-27 14:02:49');
INSERT INTO `sys_log` VALUES ('1427', 'ceshi03', '科技成果', '新增科技成果', 'com.example.busniess.controller.OccupancyController.addOccupancy()', '[{\"id\":345,\"userName\":\"ceshi03\",\"resultTechnolo\":\"利用固体废弃物做环保新型发泡建筑材料\",\"stage\":\"试验阶段\",\"advantages\":\"发明专利\",\"industry\":\"资源与环境\",\"industryDetail\":\"固体废弃物处置与综合利用技术\",\"attribute\":\"独立完成\",\"patenNname\":\"利用固体废弃物做环保新型发泡建筑材料\",\"patenNumber\":\"12373827467.23\",\"negotiable\":\"面议\",\"describe\":\"\\u003cp\\u003e\\t   \\u003cspan style\\u003d\\\"color: rgb(102, 102, 102);\\\"\\u003e采用固废废弃物为主要原料，原料来源广，可就地取材，资源消耗少。 1、热传导率低，导热系数为0.06~0.10W/(m•K) 2、轻质、高强、高硬 3、高效、可靠的保温与绝热性能 4、突出的防火阻燃及耐高温性能 5、防水、防潮、抗渗漏——可靠的刚性防水材料 6、优异的化学稳定性 7、耐冻融，耐老化、耐候性强——自然使用寿命无上限 8、显著的吸声隔音降噪性能 9、加工性能优异，尺寸稳定性一致性好，与其他材料和易性强 10、宽泛的温度使用范围 11、优异的安全性、卫生性、防护性、高度绿色环保型材料\\u003c/span\\u003e\\u003c/p\\u003e\",\"appliedRange\":\"\\u003cp\\u003e  \\t   可广泛应用于各建筑业基础建筑材料的使用，可凭借优越的材质特性在当前建筑材料市场上占有一定的市场。\\u003c/p\\u003e\",\"transferType\":\"完全转让\",\"linkMan\":\"开\",\"phoneNumber\":\"18726382637\",\"province\":\"北京市\",\"city\":\"市辖区\",\"district\":\"东城区\",\"imgAddress\":[{\"oId\":345,\"img\":\"/img/2020-01-02/863be950ccf74cafabbb3359ea11762d.png\"},{\"oId\":345,\"img\":\"\"},{\"oId\":345,\"img\":\"\"}],\"companyName\":\"国科创新\"}]', '24', '192.168.30.26', '2020-01-02 18:29:41');
